if( $show == 1 )
{
 $this_host = $this->ipsclass->my_getenv( 'SERVER_NAME' );
 $this_script = $this->ipsclass->my_getenv( 'SCRIPT_NAME' );

 if( $this_host AND $this_script )
 {
  $this_script = str_replace( '/'.IPB_ACP_DIRECTORY.'/index.php', '/uploads', $this_script );

  $r['conf_description'] = str_replace( "<!--SUGGEST-->", "<br />IP.Board определил следующий адрес директории загрузок:<br />http://".$this_host.$this_script, $r['conf_description'] );
 }
}

if( $show == 1 )
{
 $this_script = str_replace( '\\\\', '/', $this->ipsclass->my_getenv( 'SCRIPT_FILENAME' ) );

 if( $this_script )
 {
  $this_script = str_replace( '/'.IPB_ACP_DIRECTORY.'/index.php', '/uploads', $this_script );

  $r['conf_description'] = str_replace( "<!--SUGGEST-->", "<br />IP.Board определил следующий путь к директории загрузок:<br />".$this_script, $r['conf_description'] );
 }
}

if ( $show == 1 )
{

    if ( ! $this->ipsclass->DB->sql_can_fulltext() )
    {
        $full_text = "<span style='color:red'><b>Поисковая система основанная на полнотекстовом поиске недоступна на сервере.</b></span>";
    }
    else
    {
        $full_text = "<b>Доступна поисковая система основанная на полнотекстовом поиске.</b>";
    }

    if ( ! $this->ipsclass->admin->get_fulltextindex_status() )
    {
        $index = "<span style='color:red'><b>Не найдены индексы полнотекстового поиска</b></span>: <a href='{$this->ipsclass->base_url}&section=tools&act=op&code=dofulltext'>создать индексы!</a>";
    }
    else
    {
        $index = "<b>Найдены индексы полнотекстового поиска.</b>";
    }

    $r['conf_description'] = str_replace( "<!--FULLTEXT-->", $full_text, $r['conf_description'] );
    $r['conf_description'] = str_replace( "<!--FULLCHECK-->", $index, $r['conf_description'] );
}

if ( $save == 1)
{
    if ( is_array($_POST['vnp_block_forums']) )
    {
        $_POST['vnp_block_forums'] = implode(",",$_POST['vnp_block_forums']);
    }
    else
    {
        $_POST['vnp_block_forums'] = "";
    }

    $key = 'vnp_block_forums';
}
if ( $show == 1 )
